#pragma config(Motor,  port4,           BAR2,          tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port5,           BAR3,          tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port6,           BAR4,          tmotorVex393_MC29, openLoop)
